    /** Token account */
    token: TAccountMetas[2];
    /** Token account owner */
    tokenOwner: TAccountMetas[3];
    /** Destination token account */
    destination: TAccountMetas[4];
    /** Destination token account owner */
    destinationOwner: TAccountMetas[5];
    /** Mint of token asset */
